mapper.insert:
/* saveMember : */

INSERT INTO  ADMIN_USER (

    ADMIN_ID
   ,ADMIN_NM
   ,ADMIN_NO
   ,ADMIN_CELLNO
   ,ADMIN_MAIL

) VALUES (
    (SELECT MAX(ADMIN_ID)+1 FROM ADMIN_USER AS ADMIN_ID)
   ,#{adminEmpName}
   ,#{adminEmpNo}
   ,#{adminEmpMail}
   ,#{adminEmpCellNo}

)

mapper.update:
/* updateMember : Param = model.MadminMemberMain */

UPDATE
    ADMIN_USER
SET
     ADMIN_NM = #{admin_nm}
    ,ADMIN_NO = #{admin_no}
    ,ADMIN_CELLNO = #{admin_cellno}
    ,ADMIN_MAIL = #{admin_mail}
WHERE
    ADMIN_ID = #{admin_id}

mapper.delete:
/* delete : Param = model.MadminMemberMain */

DELETE

FROM
    ADMIN_USER
WHERE
    ADMIN_NM = #{admin_nm}
AND
    ADMIN_NO = #{admin_no}
AND
    ADMIN_CELLNO = #{admin_cellno}
AND
    ADMIN_MAIL = #{admin_mail}
